[[TOOL: write_file]]
[[ARG: path]]
features/gallery/script.js
[[ARG: content]]
document.addEventListener('DOMContentLoaded', function() {
    const images = document.querySelectorAll('.lightbox-image');
    let currentIndex = 0;

    images.forEach((img, index) => {
        img.addEventListener('click', () => {
            showLightbox(index);
        });
    });

    function showLightbox(index) {
        currentIndex = index;
        document.querySelector('.gallery').innerHTML = `
            <div class="lightbox">
                <span onclick="closeLightbox()" class="close-button">&times;</span>
                <img src="${images[currentIndex].src}" alt="Bild" class="lightbox-image">
                <button onclick="nextImage()" class="prev-button"> vorheriges Bild</button>
                <button onclick="nextImage()" class="next-button"> nächstes Bild</button>
            </div>
        `;
    }

    function closeLightbox() {
        document.querySelector('.gallery').innerHTML = '';
    }

    function nextImage() {
        currentIndex = (currentIndex + 1) % images.length;
        showLightbox(currentIndex);
    }
});
[[END_TOOL]]
